import { Component } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Item } from 'src/app/interface/item';
import { ItemService } from 'src/app/services/item-services/item.service';

@Component({
  selector: 'app-item-details',
  templateUrl: './item-details.component.html',
  styleUrls: ['./item-details.component.css'],
})
export class ItemDetailsComponent {
  item: Item = { id: 0, name: '', price: 0, category: '', description: '' };
  constructor(
    private route: ActivatedRoute,
    private itemService: ItemService
  ) {}
  ngOnInit(): void {
    this.getItem();
  }
  getItem(): void {
    const paramId = +this.route.snapshot.paramMap.get('id')!;
    if (!isNaN(paramId)) {
      this.itemService.getItem(paramId).subscribe((item) => (this.item = item));
    }
  }
  addToCart(): void {}
}
<h3>{{ item.name }}</h3>
<div>
  <div class="row">
    <div class="col-md-3">
      <img
        src="/EShop/ClientApp/src/assets/awwal.jpg"
        width="200px"
        alt="logo"
      />
    </div>
  </div>
  <div class="row">
    <div class="col-md-3">Description:{{ item.description }}</div>
  </div>
  <div class="row">
    <div class="col-md-3">Category: {{ item.category }}</div>
  </div>
  <div class="row">
    <div class="col-md-3">Price: {{ item.price }}</div>
  </div>
  <div class="row">
    <div class="col-md-3">
      <button (click)="addToCart()" id="addToCart">Add to Cart</button>
    </div>
  </div>
</div>
